<?phpnamespace ShuntingYard\OperatorRegistry;use ShuntingYard\OperatorRegistry\Interfaces\HasGroup;require_once('UnaryOperatorEntry.php');require_once('Interfaces/HasGroup.php');//stores a function operator and a callable to run on the operand arrayclass FunctionEntry extends UnaryOperatorEntry{    public function __construct($name, $operation)    {        parent::__construct($name, self::PREFIX, function ($operand) use ($operation) {            if ($operand instanceof HasGroup) {                return $operation($operand->getContents());            } else {                return $operation($operand);            }        });    }}