<?phpnamespace ShuntingYard\OperatorRegistry;use ShuntingYard\OperatorRegistry\Interfaces\HasGroup;require_once('UnaryOperatorEntry.php');require_once('Interfaces/HasGroup.php');//stores the open operator string valueclass OpenArrayOperatorEntry extends UnaryOperatorEntry{    /**     * @param $operator string     */    public function __construct($operator)    {        parent::__construct($operator, UnaryOperatorEntry::PREFIX, function ($operand) {            if ($operand instanceof HasGroup) {                $result = $operand->getContents();            } else {                $result = [$operand];            }            return $result;        });    }    /** @return string */    public function getOpen()    {        return parent::getName();    }}