<?phpnamespace ShuntingYard\OperatorRegistry;use ShuntingYard\OperatorRegistry\Interfaces\HasGroup;require_once('BinaryOperatorEntry.php');require_once('Group.php');require_once('Interfaces/HasGroup.php');//stores a separator operatorclass SeparatorEntry extends BinaryOperatorEntry{    public function __construct($name)    {        parent::__construct($name, self::LEFT_ASSOCIATIVE, 0, function ($operand1, $operand2) {            if ($operand1 instanceof HasGroup && $operand2 instanceof HasGroup) {                $result = $operand1;                $result->addContents($operand2->getContents());            } elseif ($operand1 instanceof HasGroup) {                $result = $operand1;                $result->addContents([$operand2]);            } elseif ($operand2 instanceof HasGroup) {                $result = new Group(array_merge([$operand1], $operand2->getContents()));            } else {                $result = new Group([$operand1, $operand2]);            }            return $result;        });    }}